import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet } from 'react-native';
import axios from 'axios';

const TicketList = () => {
  const [tickets, setTickets] = useState([]);
  const [error, setError] = useState(null);

  const api = async () => {
    try {
      const res = await axios.get('http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/Ticket', {
        headers: {
          'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
          'Session-Token': '53mki5kinndueg00epb5rnrm4s',
          'Accept-Range': 990,
        }
      });
      setTickets(res.data);
    } catch (error) {
      console.error('Erro ao carregar os tickets:', error);
      setError(error.message);
    }
  };

  useEffect(() => {
    api();
  }, []);

  const renderItem = ({ item }) => (
    <View style={styles.ticketItem}>
      <Text style={styles.ticketText}>ID: {item.id}</Text>
      <Text style={styles.ticketText}>Nome: {item.name}</Text>
      <Text style={styles.ticketText}>Urgência: {item.urgency}</Text>
      <Text style={styles.ticketText}>Comentário: {item.content}</Text>
      <Text style={styles.ticketText}>Data de Criação: {item.date_creation}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      {error ? (
        <Text style={styles.errorText}>Erro ao carregar os tickets: {error}</Text>
      ) : (
        <FlatList
          data={tickets}
          keyExtractor={item => item.id.toString()}
          renderItem={renderItem}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: "#fff",
  },
  ticketItem: {
    borderBottomWidth: 1,
    borderBottomColor: "#ddd",
    paddingVertical: 10,
  },
  ticketText: {
    fontSize: 16,
  },
  errorText: {
    color: 'red',
    fontSize: 16,
  },
});

export default TicketList;
