import index from '../Login/index';

let globalJsonData; 

const fetchData = async () => {
    try {
      const response = await fetch('http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/initSession', {
        method: "GET",
        headers: {
          'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
          'Content-Type': 'application/json',
          'Authorization': 'Basic ' + encoded
        }
      });

      if (!response.ok) {
        throw new Error('Erro ao buscar os dados');
      } 

      globalJsonData = await response.json(); 
      console.log(globalJsonData);
    } catch (error) {
      console.error(error);
    }
  };

fetchData();

class ApiGlobal {
    static sessionToken = globalJsonData; 
};

export default ApiGlobal;
