                        style={{ backgroundColor: "blue", padding: 10, borderRadius: 5 }}
                        onPress={sendMessage}
                    >
                        <Text style={{ color: "white" }}>Send</Text>
                    </TouchableOpacity>
                </View>
            </View>
        );
    };
}
export default Chat;
