import React, { useEffect, useState } from "react";
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, TextInput } from "react-native";
import logo from '../assets/logo.png'
import * as Animatable from 'react-native-animatable'
import { useNavigation } from '@react-navigation/native'
import Modal from 'react-native-modal';

export default function Projetos() {
    const navigation = useNavigation();
    const [novoProjeto, setNovoProjeto] = useState({ name: "", content: "", data: "" });
    const [modalVisivel, setModalVisivel] = useState(false);

    const openModal = () => {
        setModalVisivel(true);
    };

    const closeModal = () => {
        setModalVisivel(false);
    };

    const saveModal = () => {
        setModalVisivel(false);
        // Assuming adicionarTickets and carregarProjetos are defined somewhere
        // adicionarTickets(setNovoProjeto);
        // carregarProjetos();
        setNovoProjeto({ name: "", content: "", data: "" });
    };

    useEffect(() => {
        // Assuming carregarProjetos is defined somewhere
        // carregarProjetos();
    }, []);

    return (
        <View style={styles.background}>
            <Animatable.View style={styles.header}>
                <Animatable.Image
                    animation={"flipInY"}
                    source={logo} style={styles.image} />
            </Animatable.View>

            <Animatable.View delay={400} animation={"fadeInUp"} style={styles.barHeader}>
                <Text>Home</Text>
                <Text>/</Text>
                <Text>Ferramentas</Text>
                <Text>/</Text>
                <Text>Projetos</Text>
                <Text>/</Text>
            </Animatable.View>

            <Animatable.View delay={400} animation={"fadeInUp"} style={styles.container}>
                <Text style={styles.headerText}>Projetos</Text>
                <TouchableOpacity
                    onPress={openModal}
                    style={styles.buttonExit}>
                    <Text style={styles.text2}>Projeto +</Text>
                </TouchableOpacity>
            </Animatable.View>

            <Modal isVisible={modalVisivel} onBackdropPress={closeModal}>
                <View style={styles.modalContainer}>
                    <Text style={styles.modalHeader}>Adicione as informações do Projeto</Text>
                    <TextInput
                        style={styles.modalInput}
                        placeholder="Nome do Projeto"
                        value={novoProjeto.name}
                        onChangeText={(text) => setNovoProjeto({ ...novoProjeto, name: text })}
                    />
                    <TextInput
                        style={styles.modalInput}
                        placeholder="Descrição"
                        value={novoProjeto.content}
                        onChangeText={(text) => setNovoProjeto({ ...novoProjeto, content: text })}
                    />
                    <TouchableOpacity style={styles.modalButton} onPress={saveModal}>
                        <Text>Salvar</Text>
                    </TouchableOpacity>
                </View>
            </Modal>

            <View style={styles.containerBox}>

            </View>
            <TouchableOpacity
                onPress={() => navigation.navigate('Login')}
                style={styles.buttonExit}>
                <Text style={styles.text2}>Voltar</Text>
            </TouchableOpacity>
        </View>
    );
}

const styles = StyleSheet.create({
    background: {
        backgroundColor: '#fff',
        height: '100%'
    },
    header: {
        backgroundColor: "#fff",
    },
    barHeader: {
        backgroundColor: "#498DF3",
        flexDirection: 'row',
        justifyContent: 'space-evenly',
    },
    headerText: {
        margin: '5%',
        fontSize: 20,
        fontWeight: 'bold',
    },
    container: {
        alignItems: "center",
        justifyContent: "space-between",
        flexDirection: 'row'
    },
    image: {
        margin: 8,
        alignSelf: 'center'
    },
    text1: {
        fontSize: 20,
    },
    text2: {
        fontSize: 15,
    },
    buttonExit: {
        backgroundColor: '#498DF3',
        width: 70,
        justifyContent: 'end',
        alignItems: "center",
        textAlign: "center",
        marginRight: '5%',
        borderRadius: 3,
    },
    containerBox: {
        backgroundColor: '#ddd',
        height: '70%',
        margin: '5%'
    },
    modalContainer: {
        backgroundColor: "#fff",
        padding: 20,
        borderRadius: 10,
    },
    modalHeader: {
        fontSize: 18,
        fontWeight: "bold",
        marginBottom: 10,
    },
    modalInput: {
        height: 40,
        borderColor: "gray",
        borderWidth: 1,
        marginBottom: 10,
        paddingHorizontal: 10,
    },
    modalButton: {
        backgroundColor: "#3273D4",
        borderRadius: 6,
        padding: 10,
        alignItems: "center",
    },
});
