import React, { useState, useEffect } from "react";
import { View, Text, TextInput, TouchableOpacity, FlatList, StyleSheet } from "react-native";
import Modal from 'react-native-modal';
import AsyncStorage from "@react-native-async-storage/async-storage";

const TicketCrud = async () => {
  const storedSessionToken = await AsyncStorage.getItem('sessionToken');
        setSessionToken(storedSessionToken);

      const [, tokenPart] = storedSessionToken.replace(/[{}]/g, '').split(':');
      const TokenObjetc = JSON.parse(tokenPart)
  const [tickets, setTickets] = useState([]);
  const [newTicket, setNewTicket] = useState({ name: "", local: "", urgency: "" });
  const [isModalVisible, setModalVisible] = useState(false);
  const [expandedItem, setExpandedItem] = useState(null);
  const [sortOrder, setSortOrder] = useState("default");
  const [searchId, setSearchId] = useState("");
  const [sessionToken, setSessionToken] = useState(null);


  const getUrgencyColor = (urgency) => {
    switch (urgency) {
      case 1:
        return { backgroundColor: '#96be25' };
      case 2:
        return { backgroundColor: '#F1b938' };
      case 3:
        return { backgroundColor: '#C1232e' };
      default:
        return { backgroundColor: '#000' };
    }
  };

  useEffect(() => {
    loadTickets();
  }, [sortOrder]);

  const loadTickets = async () => {
    try {
      const response = await fetch("http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/Ticket", {
        method: "GET",
        headers: {
          'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
          'Session-Token': `${TokenObjetc}`,
        },
      });

      if (response.ok) {
        let data = await response.json();
        if (sortOrder === "alphabetical") {
          data = data.sort((a, b) => a.name.localeCompare(b.name));
        }
        setTickets(data);
      } else {
        console.error("Falha em acessar a API");
      }

    } catch (error) {
      console.error("Erro ao carregar a API:", error);
    }
  };
  const addTicket = async () => {
    try {
  const response =  await fetch ("http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/Ticket",{
      method: "POST",
      headers: {
        'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
        'Session-Token': `${TokenObjetc}`,
      },
       body: JSON.stringify({
          input: {
            name: newTicket.name,
            urgency: newTicket.urgency,
            content: newTicket.content,
          },
        }),
    })
    if (response.ok) {
      setNewTicket({ name: "", content: "", urgency: "" });
      loadTickets();
    } else {
      console.error("Failed to add ticket");
    }
  } catch (error) {
    console.error("Error adding ticket:", error);
  }
};
  const searchTicketById = () => {
    if (!searchId.trim()) {
      loadTickets();
      return;
    }

    const foundTicket = tickets.find(ticket => ticket.id === parseInt(searchId));
    if (foundTicket) {
      setTickets([foundTicket]);
    } else {
      alert("Ticket nÃ£o encontrado")
    }
  };  

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };
const saveModal = () =>{
  setModalVisible(false);
  addTicket(newTicket);
  setNeWTicket({name:"", content:"",urgency:""});
  loadTickets();
}

  const toggleItem = (id) => {
    setExpandedItem((prevItem) => (prevItem === id ? null : id));
  };
  const toggleSortOrder = () => {
    const newOrder = sortOrder ===  "default" ? "alphabetical" : "default";
    setSortOrder(newOrder);
  };
  useEffect(() => {
    if (sortOrder === "urgency") {
      const sortedTickets = [...tickets].sort((a, b) => a.urgency - b.urgency);
      setTickets(sortedTickets);
    } else {
      loadTickets();
    }
  }, [sortOrder]);
  const cleanID = () =>
  {
    setSearchId("")
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.button} onPress={openModal}>
        <Text >Abrir Ticket</Text>
      </TouchableOpacity>
      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.filterButton} onPress={toggleSortOrder}>
          <Text style={styles.filterButtonText}>
            {sortOrder === "default" ? "ID -> A-Z" : "A-Z -> ID"}
          </Text>
        </TouchableOpacity>

        <View style={styles.searchContainer}>
          <TextInput
            style={styles.searchInput}
            placeholder="----"
            value={searchId}
            onChangeText={setSearchId}
            keyboardType="numeric"
          />
          <TouchableOpacity style={styles.searchButton} onPress={searchTicketById}>
            <Text style={styles.idText}>Pesquisar ID</Text>
          </TouchableOpacity>
        </View>

      </View>

      <View style={styles.urgencyText}>
        <Text>UrgÃªncia:</Text>
        <Text style={styles.descUrgency1}> Baixa </Text>
        <Text style={styles.descUrgency2}> MÃ©dia </Text>
        <Text style={styles.descUrgency3}>Alta</Text>

      <TouchableOpacity 
      style={styles.cleanButton}
      onPress={cleanID}
      >
      <Text style={styles.cleanButtonText}>Limpar Id</Text>
      </TouchableOpacity>


      </View>
      <FlatList
        data={tickets}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.ticketItem}>
            <TouchableOpacity onPress={() => toggleItem(item.id)}>
              <View style={styles.ticketContent}>
                <Text>{item.name}</Text>
                <View style={[styles.urgencyIndicator, getUrgencyColor(item.urgency)]}>
                  <Text style={styles.urgencyNumber}>{item.urgency}</Text>
                </View>
              </View>
            </TouchableOpacity>
            {expandedItem === item.id && (
              <View style={styles.expandedContent}>
                <Text>ID - {item.id}</Text>
                <Text>Local - {item.content}</Text>
                <Text>Data - {item.date_creation}</Text>
              </View>
            )}
          </View>
        )}
      /><Modal isVisible={isModalVisible} onBackdropPress={closeModal}>
      <View style={styles.modalContainer}>
        <Text style={styles.modalHeader}>Adicione as informaÃ§Ãµes</Text>
        <TextInput
          style={styles.modalInput}
          placeholder="Nome"
          value={newTicket.name}
          onChangeText={(text) => setNewTicket({ ...newTicket, name: text })}
        />
        <TextInput
          style={styles.modalInput}
          placeholder="Local"
          value={newTicket.content}
          onChangeText={(text) => setNewTicket({ ...newTicket, content: text })}
        />
        <TextInput
          style={styles.modalInput}
          placeholder="UrgÃªncia"
          value={newTicket.urgency}
          onChangeText={(text) => setNewTicket({ ...newTicket, urgency: text })}
        />
        <TouchableOpacity style={styles.modalButton} onPress={saveModal}>
          <Text>Salvar</Text>
        </TouchableOpacity>
      </View>
    </Modal>
    </View>

  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: "#fff",
  },
  button: {
    backgroundColor: "#498DF3",
    borderRadius: 6,
    padding: 10,
    alignItems: "center",
    marginBottom: 20,
  },
  ticketItem: {
    flexDirection: "column",
    borderColor: "#ddd",
    borderWidth: 1,
    padding: 10,
    marginBottom: 10,
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 10,
  },
  idText: {
    color: '#fff'
  },
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'flex-end',
    bottom: 5
  },
  searchInput: {
    height: 40,
    width: '20%',
    borderColor: "gray",
    borderWidth: 1,
    paddingHorizontal: 10,
    marginRight: 10,
  },
  searchButton: {
    backgroundColor: "#3273D4",
    borderRadius: 6,
    padding: 10,
    alignItems: "center",
  },
  cleanButton: {
    borderRadius: 6,
    padding: 10,
  },
  cleanButtonText: {
    color: "#484848",
    bottom: '50%',
    textDecorationLine: 'underline',
    marginLeft: '42%'
  },
  filterButton: {
    backgroundColor: "#3273D4",
    borderRadius: 6,
    padding: 10,
    width: '35%',
    alignItems: "center",
    marginBottom: 10,
  },
  filterButtonText: {
    color: "#fff",
  },
  ticketContent: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  urgencyIndicator: {
    width: 25,
    height: 25,
    borderRadius: 15,
    justifyContent: "center",
    alignItems: "center",
    marginRight: 10,
  },
  descUrgency1: {
    color: "white",
    backgroundColor: "#96be25",
    width: 45,
    textAlign: "center",
    height: 18,
    borderRadius: 15,
