import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet, Alert } from "react-native";
import { useNavigation } from '@react-navigation/native';
import * as Animatable from 'react-native-animatable';
import AsyncStorage from "@react-native-async-storage/async-storage";
import logo from '../assets/logo.png';
import gear from '../assets/gear.png';
import axios from 'axios'; // Importar o Axios

const Login = () => {
    const navigation = useNavigation();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async () => {
        const texto = `${username}:${password}`;
        const encoded = Buffer.from(texto).toString('base64');
        console.log('token ' + encoded);

        try {
            const response = await axios.get('http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/initSession', {
                headers: {
                    'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
                    'Authorization': 'Basic ' + encoded
                }
            });

            const json = response.data;

            if (!username || !password) {
                Alert.alert('Erro', 'Preencha todos os campos');
                return;
            }

            if (json && json[0] === 'ERROR_GLPI_LOGIN') {
                Alert.alert('Erro', 'Nome de usuário ou senha inválidos. Tente novamente.');
            } else {
                await AsyncStorage.setItem('sessionToken', JSON.stringify(json));
                await AsyncStorage.setItem('Credenciais', JSON.stringify({ username, password }));
                navigation.navigate('Serviços');
            }
        } catch (error) {
            console.error(error);
            Alert.alert('Erro', 'Falha ao autenticar. Verifique suas credenciais e tente novamente.');
        }
    };

    return (
        <>
            <View style={styles.container}>
                <View>
                    <Animatable.Image
                        animation={"flipInY"}
                        source={logo} style={styles.image} />
                </View>
            </View>
            <View>
                <Text style={styles.texto}>
                    Usuário:
                </Text>
                <TextInput
                    placeholder="Login..."
                    style={styles.input}
                    onChangeText={setUsername}
                    value={username}
                />
                <Text style={styles.bar}></Text>
                <Text style={styles.texto}>
                    Senha:
                </Text>
                <TextInput
                    placeholder="Senha..."
                    style={styles.input}
                    onChangeText={setPassword}
                    value={password}
                    secureTextEntry={true}
                />
                <Text style={styles.bar}></Text>

                <TouchableOpacity style={styles.button} onPress={handleLogin}>
                    <Text>Entrar</Text>
                </TouchableOpacity>
            </View>
            <View>
                <TouchableOpacity style={styles.gear} onPress={() => navigation.navigate('Servidores')}>
                    <Image source={gear} style={styles.gear} />
                </TouchableOpacity>
            </View>
        </>
    );
}

const styles = StyleSheet.create({
    container: {
        backgroundColor: "#fff",
    },
    image: {
        margin: 8,
        alignSelf: 'center'
    },
    texto: {
        fontSize: 26,
        marginLeft: 16,
        padding: 16,
        fontWeight: "bold",
    },
    bar: {
        borderBottomWidth: 1,
        borderBottomColor: "#434343",
        width: "85%",
        justifyContent: "center",
        alignSelf: "center",
        bottom: 20
    },
    input: {
        fontSize: 16,
        marginLeft: 16,
        padding: 16,
        width: "90%"
    },
    button: {
        backgroundColor: "#FFE382",
        padding: 16,
        borderRadius: 6,
        width: "45%",
        alignSelf: "center",
        alignItems: "center",
        marginVertical: "15%",
        shadowColor: "#000",
        shadowOpacity: 0.23,
        shadowRadius: 2.62,
        elevation: 4,
        shadowOffset: {
            width: 0,
            height: 2,
        },
    },
    gear: {
        width: 90,
        height: 90,
        alignSelf: "flex-end",
        bottom: "75%"
    },
});

export default Login;
