import React, { useState, useEffect } from "react";
import { View, Text, Image, StyleSheet, TouchableOpacity, TextInput, Alert } from "react-native";
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from "@react-native-async-storage/async-storage";
import base64 from 'base-64';

import logo from '../assets/logo.png';
import gear from '../assets/gear.png';

const Login = () => {
    const navigation = useNavigation();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [selectedOption, setSelectedOption] = useState('');


    //function para dar ou não ADM pro user dependendo da conta dele 
    const option = async (TI, BC) => {
        switch (setSelectedOption){
            case TI :
                await AsyncStorage.setItem('TI');
                console.log("TI")
                case BC: 
                await AsyncStorage.setItem('BC');
                console.log("BC")
        }
    }

//limpa as routes sempre que inicia o app de novo
    const clearSavedPages = async () => {
        try {
            await AsyncStorage.removeItem('Computer');
            await AsyncStorage.removeItem('Ticket');
            await AsyncStorage.removeItem('Printer');
            // console.log('Valores salvos limpos com sucesso.');
        } catch (error) {
            console.error('Erro ao limpar os valores salvos:', error);
        }
    };

    useEffect(() => {
        clearSavedPages();
    }, []);


    const handleLogin = async () => {
        if (!username || !password || !selectedOption) {
            Alert.alert('Preencha todos os campos e selecione uma opção');
            return;
        }

        const text = `${username}:${password}`;
        const encoded = base64.encode(text);
        //console.log('token ' + encoded);
        await AsyncStorage.setItem('encoded', encoded);

        try {
            const res = await fetch('http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/initSession', {
                method: "GET",
                headers: {
                    'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
                    'Authorization': 'Basic ' + encoded
                },
            });

            const json = await res.json();
            // console.log(json);

            if (json && json[0] === 'ERROR_GLPI_LOGIN') {
                Alert.alert('Erro', 'Nome de usuário ou senha inválidos');
            } else {
                await AsyncStorage.setItem('sessionToken', JSON.stringify(json));
                await AsyncStorage.setItem('Credenciais', JSON.stringify({ username, password }));
                navigation.navigate('Serviços');
            }
        } catch (error) {
            console.error(error);
            Alert.alert('Erro', 'Falha ao autenticar. Verifique suas credenciais e tente novamente.');
        }
    };

    return (
        <>
            <View style={styles.containerHeader}>
                <View>
                    <Image
                        animation={"flipInY"}
                        source={logo} style={styles.image} />
                </View>
            </View>

            <View>
                <View style={styles.containerLogin}>
                    <Text style={styles.text}>
                        Usuário:
                    </Text>
                    <TextInput
                        placeholder="Login..."
                        style={styles.input}
                        onChangeText={setUsername}
                        value={username}
                    />
                    <Text style={styles.bar}></Text>
                    <Text style={styles.text}>
                        Senha:
                    </Text>

                    <TextInput
                        placeholder="Senha..."
                        style={styles.input}
                        onChangeText={setPassword}
                        value={password}
                        secureTextEntry={true}
                    />
                    <Text style={styles.bar}></Text>

                    <View style={styles.optionsContainer}>
                        <TouchableOpacity 
                            style={[styles.optionButton, selectedOption === 'TI' && styles.selectedOption]} 
                            onPress={() => setSelectedOption('TI')}>
                            <Text style={styles.optionText}>TI</Text>
                        </TouchableOpacity>
                        <TouchableOpacity 
                            style={[styles.optionButton, selectedOption === 'Banco Interno' && styles.selectedOption]} 
                            onPress={() => setSelectedOption('Banco Interno')}>
                            <Text style={styles.optionText}>Banco Interno</Text>
                        </TouchableOpacity>
                    </View>

                    <TouchableOpacity style={styles.button} onPress={handleLogin}>
                        <Text>Entrar</Text>
                    </TouchableOpacity>

                </View>
                <View>
                    <TouchableOpacity style={styles.gear} onPress={() => navigation.navigate('Servidores')}>
                        <Image source={gear} style={styles.gear} />
                    </TouchableOpacity>
                </View>
            </View>
        </>
    );
};

const styles = StyleSheet.create({
    containerHeader: {
        backgroundColor: "#fff",
    },
    containerLogin: {
        backgroundColor: "#fff",
        height: '85%',
        margin: 16,
    },
    image: {
        margin: 8,
        alignSelf: 'center'
    },
    text: {
        fontSize: 26,
        marginLeft: 16,
        padding: 16,
        fontWeight: "bold",
    },
    bar: {
        borderBottomWidth: 1,
        borderBottomColor: "#434343",
        width: "85%",
        justifyContent: "center",
        alignSelf: "center",
        bottom: 20
    },
    input: {
        fontSize: 16,
        marginLeft: 16,
        padding: 16,
        width: "90%"
    },
    button: {
        backgroundColor: "#FFE382",
        padding: 16,
        borderRadius: 6,
        width: "45%",
        alignSelf: "center",
        alignItems: "center",
        marginVertical: "5%",
        shadowColor: "#000",
        shadowOpacity: 0.23,
        shadowRadius: 2.62,
        elevation: 4,
        shadowOffset: {
            width: 0,
            height: 2,
        },
    },
    gear: {
        width: 90,
        height: 90,
        alignSelf: "flex-end",
        bottom: "75%"
    },
    optionsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        marginVertical: 20,
    },
    optionButton: {
        padding: 10,
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
    },
    selectedOption: {
        backgroundColor: '#FFE382',
    },
    optionText: {
        fontSize: 16,
    },
});

export default Login;
