import React, { useState, useEffect } from "react";
import { View, Text, Image, StyleSheet, TouchableOpacity, TextInput, Alert } from "react-native";
import { useNavigation } from '@react-navigation/native';
import * as Animatable from 'react-native-animatable';
import AsyncStorage from "@react-native-async-storage/async-storage";
import utf8 from 'utf8';
import base64 from 'base-64';

import logo from '../assets/logo.png';
import gear from '../assets/gear.png';

const Login = () => {
    const navigation = useNavigation();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [saveUser, setSaveUser] = useState(false); 
    const [selectedOption, setSelectedOption] = useState('');

    const clearSavedPages = async () => {
        try {
            await AsyncStorage.removeItem('Computer');
            await AsyncStorage.removeItem('Ticket');
            await AsyncStorage.removeItem('Printer');
            //console.log('Valores salvos limpos com sucesso.');
        } catch (error) {
            console.error('Erro ao limpar os valores salvos:', error);
        }
    };

    useEffect(() => {
        clearSavedPages();
        const unsubscribe = navigation.addListener('beforeRemove', () => {
            AsyncStorage.removeItem('Credenciais');
        });

        return unsubscribe;
    }, []);

    const saveNewCredentials = async () => {
        try {
            const newCredentials = { username, password };
            const storedUsers = await AsyncStorage.getItem('Users');
            let usersArray = storedUsers ? JSON.parse(storedUsers) : [];

            const userExists = usersArray.some(user => user.username === username);
            if (userExists) {
                Alert.alert('Usuário já existe. Tente outro');
                return;
            }

            usersArray.push(newCredentials);
            await AsyncStorage.setItem('Users', JSON.stringify(usersArray));
            Alert.alert('Novas credenciais salvas com sucesso!');
        } catch (error) {
            Alert.alert('Falha ao salvar novas credenciais');
        }
    };

    const handleLogin = async () => {
        const texto = `${username}:${password}`;
        const bytes = utf8.encode(texto);
        const encoded = base64.encode(bytes);
        //console.log('token ' + encoded);
        await AsyncStorage.setItem('encoded', encoded);
        if (!username || !password || !selectedOption) {
            Alert.alert('Preencha todos os campos e selecione uma opção');
            return;
        }
        try {
            const res = await fetch('http://ti.ararangua.sc.gov.br:10000/glpi/apirest.php/initSession', {
                method: "GET",
                headers: {
                    'App-Token': 'D8lhQKHjvcfLNrqluCoeZXFvZptmDDAGhWl17V2R',
                    'Authorization': 'Basic ' + encoded
                },
            });
            const json = await res.json();
            //console.log(json);

            if (json && json[0] === 'ERROR_GLPI_LOGIN') {
                Alert.alert('Erro', 'Nome de usuário ou senha inválidos');
            } else {
                await AsyncStorage.setItem('sessionToken', JSON.stringify(json));
                await AsyncStorage.setItem('Credenciais', JSON.stringify({ username, password }));
                if (saveUser) {
                    await AsyncStorage.setItem('user', username); 
                }
                navigation.navigate('Serviços');
            }
        } catch (error) {
            console.error(error);
            Alert.alert('Verifique suas credenciais e tente novamente');
        }
    };

    return (
        <>
            <View style={styles.containerHeader}>
                <View>
                    <Animatable.Image
                        animation={"flipInY"}
                        source={logo} style={styles.image} />
                </View>
            </View>

            <View>
                <View style={styles.containerLogin}>
                    <Text style={styles.texto}>
                        Usuário:
                    </Text>
                    <TextInput
                        placeholder="Login..."
                        style={styles.input}
                        onChangeText={setUsername}
                        value={username}
                    />
                    <Text style={styles.bar}></Text>
                    <Text style={styles.texto}>
                        Senha:
                    </Text>

                    <TextInput
                        placeholder="Senha..."
                        style={styles.input}
                        onChangeText={setPassword}
                        value={password}
                        secureTextEntry={true}
                    />
                    <Text style={styles.bar}></Text>

                    <View style={styles.optionsContainer}>
                        <TouchableOpacity 
                            style={[styles.optionButton, selectedOption === 'TI' && styles.selectedOption]} 
                            onPress={() => setSelectedOption('TI')}>
                            <Text style={styles.optionText}>TI</Text>
                        </TouchableOpacity>
                        <TouchableOpacity 
                            style={[styles.optionButton, selectedOption === 'Banco Interno' && styles.selectedOption]} 
                            onPress={() => setSelectedOption('Banco Interno')}>
                            <Text style={styles.optionText}>Banco Interno</Text>
                        </TouchableOpacity>
                    </View>

                    <TouchableOpacity style={styles.button} onPress={handleLogin}>
                        <Text>Entrar</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style={styles.button} onPress={saveNewCredentials}>
                        <Text>Salvar Novas Credenciais</Text>
                    </TouchableOpacity>
                </View>
                <View>
                    <TouchableOpacity style={styles.gear} onPress={() => navigation.navigate('Servidores')}>
                        <Image source={gear} style={styles.gear} />
                    </TouchableOpacity>
                </View>
            </View>
        </>
    );
};

const styles = StyleSheet.create({
    containerHeader: {
        backgroundColor: "#fff",
    },
    containerLogin: {
        backgroundColor: "#fff",
        height: '85%',
        margin: 16,
    },
    image: {
        margin: 8,
        alignSelf: 'center'
    },
    texto: {
        fontSize: 26,
        marginLeft: 16,
        padding: 16,
        fontWeight: "bold",
    },
    bar: {
        borderBottomWidth: 1,
        borderBottomColor: "#434343",
        width: "85%",
        justifyContent: "center",
        alignSelf: "center",
        bottom: 20
    },
    input: {
        fontSize: 16,
        marginLeft: 16,
        padding: 16,
        width: "90%"
    },
    checkbox: {
        alignSelf: 'center',
    },
    checkboxContainer: {
        flexDirection: 'row',
        marginBottom: 20,
    },
    button: {
        backgroundColor: "#FFE382",
        padding: 16,
        borderRadius: 6,
        width: "45%",
        alignSelf: "center",
        alignItems: "center",
        marginVertical: "5%",
        shadowColor: "#000",
        shadowOpacity: 0.23,
        shadowRadius: 2.62,
        elevation: 4,
        shadowOffset: {
            width: 0,
            height: 2,
        },
    },
    gear: {
        width: 90,
        height: 90,
        alignSelf: "flex-end",
        bottom: "75%"
    },
    optionsContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        marginVertical: 20,
    },
    optionButton: {
        padding: 10,
        borderWidth: 1,
        borderColor: '#ccc',
        borderRadius: 5,
    },
    selectedOption: {
        backgroundColor: '#d3d3d3',
    },
    optionText: {
        fontSize: 16,
    },
});

export default Login;
