import React, { useState, useEffect } from "react";
import { View, Text, Image, StyleSheet, TouchableOpacity, TextInput, Alert} from "react-native";
import { useNavigation } from '@react-navigation/native'
import * as Animatable from 'react-native-animatable'
import AsyncStorage from "@react-native-async-storage/async-storage";
import logo from '../assets/logo.png'
import gear from '../assets/gear.png'
import handleLogin from "../Components/ApiGlobal";

const Login = () => {
    const navigation = useNavigation();
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const loadStoredCredentials = async () => {
        try {
            const storedData = await AsyncStorage.getItem('Credenciais');
            if (storedData) {
                const parsedData = JSON.parse(storedData);
                setUsername(parsedData.username || '');
                setPassword(parsedData.password || '');
            }} 
            catch (error) { console.error("Erro ao carregar credenciais salvas:", error)}};

    const saveNewCredentials = async () => {
        try {
            const newCredentials = { username, password };
            await AsyncStorage.setItem('Credenciais', JSON.stringify(newCredentials));
            Alert.alert('Sucesso', 'Novas credenciais salvas com sucesso!');
        } catch (error) { Alert.alert('Erro', 'Falha ao salvar novas credenciais.')}};
   handleLogin;
    return (
        <>
            <View style={estilos.container}>
                <View>
                    <Animatable.Image
                        animation={"flipInY"}
                        source={logo} style={estilos.image} />
                </View>
            </View>
            <View>
                <Text style={estilos.texto}>
                    Usu√°rio:
                </Text>
                <TextInput
                    placeholder="Login..."
                    style={estilos.input}
                    onChangeText={setUsername}
                    value={username}
                />
                <Text style={estilos.bar}></Text>
                <Text style={estilos.texto}>
                    Senha:
                </Text>
                <TextInput
                    placeholder="Senha..."
                    style={estilos.input}
                    onChangeText={setPassword}
                    value={password}
                    secureTextEntry={true}
                />
                <Text style={estilos.bar}></Text>

                <TouchableOpacity style={estilos.button} onPress={handleLogin}>
                    <Text>Entrar</Text>
                </TouchableOpacity>
                <TouchableOpacity style={estilos.button} onPress={saveNewCredentials}>
                    <Text>Salvar Novas Credenciais</Text>
                </TouchableOpacity>
            </View>
            <View>
                <TouchableOpacity style={estilos.gear} onPress={() => navigation.navigate('Servidores')}>
                    <Image source={gear} style={estilos.gear} />
                </TouchableOpacity>
            </View>
        </>
    );
}

const estilos = StyleSheet.create({
    container: {
        backgroundColor: "#fff",
    },
    image: {
        margin: 8,
        alignSelf: 'center'
    },
    texto: {
        fontSize: 26,
        marginLeft: 16,
        padding: 16,
        fontWeight: "bold",
    },
    bar: {
        borderBottomWidth: 1,
        borderBottomColor: "#434343",
        width: "85%",
        justifyContent: "center",
        alignSelf: "center",
        bottom: 20
    },
    input: {
        fontSize: 16,
        marginLeft: 16,
        padding: 16,
        width: "90%"
    },
    button: {
        backgroundColor: "#FFE382",
        padding: 16,
        borderRadius: 6,
        width: "45%",
        alignSelf: "center",
        alignItems: "center",
        marginVertical: "15%",
        shadowColor: "#000",
        shadowOpacity: 0.23,
        shadowRadius: 2.62,
        elevation: 4,
        shadowOffset: {
            width: 0,
            height: 2,
        },
    },
    gear: {
        width: 90,
        height: 90,
        alignSelf: "flex-end",
        bottom: "75%"
    },
});

export default Login;
